import React,{useState} from 'react';
import './Gallery.css'
import CloseIcon from '@mui/icons-material/Close';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';

const Gallery = (inputData) =>{

    const data = inputData.data;
    const [model, setModel] = useState(false);
    const [index, setIndex] = useState(0);
    const [direction, setDirection] = useState(true);
    const getImg = (index) =>{
        setModel(true);
        setIndex(index);
    }

    const nextImg = () => {
        index === data.length - 1 ? setIndex(0) : setIndex(index + 1);
        setDirection(false);
    }

    const prevImg = () => {
        index === 0 ? setIndex(data.length-1) : setIndex(index - 1);
        setDirection(true);
    }

    return(

        <>
            <div className={model ? "model open": "model"}>
                <img src={data[index].imgSrc} className={direction ? "left" : "right"}/>
                <CloseIcon className='closeButton' onClick={()=>setModel(false)}/>
                <ArrowForwardIosIcon className='nextButton'onClick={() => nextImg()}/>
                <ArrowBackIosIcon className='backButton'onClick={() => prevImg()}/>
            </div>
            
            <div className="gallery">
                {data.map((item, index)=>{
                    return(
                    <div className='pics' key={index} onClick={()=>getImg(index)}>
                        <img src={item.imgSrc} style={{width: '100%'}}/>
                    </div>)
                })}
            </div>
        </>
    )

}

export default Gallery;
