import Gallery from './Galerry';
import React, {useState, useEffect} from 'react';

function App() {

  const url = new URL(window.location.href);
  const set = url.searchParams.get('set');
  const imgs = url.searchParams.get('imgs');

  const getImgs = async() => {
    const recieve = await fetch(`http://localhost:5000/get/${set}/${imgs}`);
    const arrayData = (await recieve.text());
    data = (arrayData.substring(1, arrayData.length-1).split(','));
    return(data)
  }

  // useEffect(() => {getImgs()});
  console.log(getImgs(data));
  return (
    <>
      <h1 style={{textAlign:'center'}}>Image Gallery</h1>
      {/* <Gallery data={data}/> */}
    </>
  );
}

export default App;
