import React,{useState} from 'react';
import './Gallery.css'
import img1 from './Cerestia/1.png';
import img2 from './Cerestia/2.png';
import img3 from './Cerestia/3.png';
import img4 from './Cerestia/4.png';
import img5 from './Cerestia/5.png';
import img6 from './Cerestia/6.png';
import CloseIcon from '@material-ui/icons/Close';

const Gallery = () =>{

    let data=[
        {
            id:1,
            imgSrc: img1,
        },
        {
            id:2,
            imgSrc: img2,
        },
        {
            id:3,
            imgSrc: img3,
        },
        {
            id:4,
            imgSrc: img4,
        },
        {
            id:5,
            imgSrc: img5,
        },
        {
            id:6,
            imgSrc: img6,
        }
    ]

    let model = Array(data.length()).fill(false);
    // const [model, setModel] = useState(false);
    // const [tempimgSrc, setTempImgSrc] = useState('');

    const getImg = (index) =>{
        model[index] = true;
    }

    return(

        <>
            {/* <div className={model ? "model open": "model"}>
                <img src={tempimgSrc}/>
                <CloseIcon onClick={()=>setModel(false)}/>
            </div> */}
            
            {data.map((item, index)=>{
                    return(
                    <div className={model[index] ? "model open" : "model"}>
                        <img src={item.imgSrc}/>
                    </div>)
            })}

            <div className="gallery">
                {data.map((item, index)=>{
                    return(
                    <div className='pics' key={index} onClick={()=>getImg(index)}>
                        <img src={item.imgSrc} style={{width: '100%'}}/>
                    </div>)
                })}
            </div>
        </>
    )

}

export default Gallery
