import Gallery from './Galerry';
import React, {useState, useEffect} from 'react';

function App(url) {

  const modifiableUrl = new URL(url);
  const set = modifiableUrl.searchParams.get('set');
  const imgs = modifiableUrl.searchParams.get('imgs');
  const [data, setData] = useState();

  const getImgs = async() => {
    const recieve = await fetch(`http://localhost:5000/get/${set}/${imgs}`);
    setData(recieve);
  }
  useEffect(() => {
    getImgs();
  })

  return (
    <>
      <h1 style={{textAlign:'center'}}>Image Gallery</h1>
      {/* <Gallery data={data}/> */}
      <h2>{data}</h2>
    </>
  );
}

export default App;
