import React,{useState} from 'react';
import './Gallery.css'
// import img1 from './Cerestia/1.png';
// import img2 from './Cerestia/2.png';
// import img3 from './Cerestia/3.png';
// import img4 from './Cerestia/4.png';
// import img5 from './Cerestia/5.png';
// import img6 from './Cerestia/6.png';
// import CloseIcon from '@material-ui/icons/Close';
import CloseIcon from '@mui/icons-material/Close';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';

const Gallery = (data) =>{

    console.log(data);
    // let data=[
    //     {imgSrc: img1},
    //     {imgSrc: img2},
    //     {imgSrc: img3},
    //     {imgSrc: img4},
    //     {imgSrc: img5},
    //     {imgSrc: img6}
    // ]
    const [model, setModel] = useState(false);
    const [index, setIndex] = useState(0);

    const getImg = (index) =>{
        setModel(true);
        setIndex(index);
    }

    const nextImg = () => {
        index === data.length - 1 ? setIndex(0) : setIndex(index + 1);
    }

    const prevImg = () => {
        index === 0 ? setIndex(data.length-1) : setIndex(index - 1);
    }

    return(

        <>
            <div className={model ? "model open": "model"}>
                <img src={data[index].imgSrc}/>
                <CloseIcon className='closeButton' onClick={()=>setModel(false)}/>
                <ArrowForwardIosIcon className='nextButton'onClick={() => nextImg()}/>
                <ArrowBackIosIcon className='backButton'onClick={() => prevImg()}/>
            </div>
            
            <div className="gallery">
                {data.map((item, index)=>{
                    return(
                    <div className='pics' key={index} onClick={()=>getImg(index)}>
                        <img src={item.imgSrc} style={{width: '100%'}}/>
                    </div>)
                })}
            </div>
        </>
    )

}

export default Gallery;
