import React,{useState} from 'react';
import './Gallery.css'
import CloseIcon from '@mui/icons-material/Close';

import img1 from './Cerestia/1.png';
import img2 from './Cerestia/2.png';
import img3 from './Cerestia/3.png';
import img4 from './Cerestia/4.png';
import img5 from './Cerestia/5.png';
import img6 from './Cerestia/6.png';

const Gallery = () =>{

    let data=[
        {
            id:1,
            imgSrc: img1,
        },
        {
            id:2,
            imgSrc: img2,
        },
        {
            id:3,
            imgSrc: img3,
        },
        {
            id:4,
            imgSrc: img4,
        },
        {
            id:5,
            imgSrc: img5,
        },
        {
            id:6,
            imgSrc: img6,
        }
    ]


    const [model, setModel] = useState(false);
    const [tempimgSrc, setTempImgSrc] = useState('');

    const getImg = (imgSrc) =>{
        setTempImgSrc(imgSrc);
        setModel(true);
    }

    return(

        <>
            <div className={model ? "model open": "model"}>
                <img src={tempimgSrc}/>
            </div>

            <div className="gallery">
                {data.map((item, index)=>{
                    return(
                    <div className='pics' key={index} onClick={()=>getImg(item.imgSrc)}>
                        <img src={item.imgSrc} style={{width: '100%'}}/>
                    </div>)
                })}
            </div>
        </>
    )

}

export default Gallery
