import React,{useState} from 'react';
import './Gallery.css'
import CloseIcon from '@mui/icons-material/Close';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import modified from './functions';
import LazyLoad from 'react-lazyload'

const Gallery = (inputData) =>{

    let data = inputData.data.replace('[', '').replace(']', '').split(',');
    data = modified(data);
    const [model, setModel] = useState(Array(data.length).fill(false));
    // const [index, setIndex] = useState(0);
    const getImg = (index) =>{
        let newModel = Array(data.length).fill(false);
        newModel[index] = true;
        setModel(newModel);
    }

    // const nextImg = () => {
    //     index === data.length - 1 ? setIndex(0) : setIndex(index + 1);
    // }

    // const prevImg = () => {
    //     index === 0 ? setIndex(data.length-1) : setIndex(index - 1);
    // }

    return(

        <>
            {/* <div className={model ? "model open": "model"}>
                <img src={require(`./storage/Main/${data[index]}`)}/>
                <CloseIcon className='closeButton' onClick={()=>setModel(false)}/>
                <ArrowForwardIosIcon className='nextButton'onClick={() => nextImg()}/>
                <ArrowBackIosIcon className='backButton'onClick={() => prevImg()}/>
            </div> */}
            <div className="model">
                {data.map((item, index)=>{
                    return(
                    <LazyLoad once = {true} placeholder = {<Loading/>}>
                        <div className={model[index] ? "model open": "model"}>
                            <img src={require(`./storage/Thumbnail/${item}`)} style={{width: '100%'}}/>
                        </div>
                    </LazyLoad>
                    )
                })}
            </div>
            
            <div className="gallery">
                {data.map((item, index)=>{
                    return(
                    <LazyLoad once = {true} placeholder = {<Loading/>}>
                        <div className='pics' key={index} onClick={()=>getImg(index)}>
                            <img src={require(`./storage/Thumbnail/${item}`)} style={{width: '100%'}}/>
                        </div>
                    </LazyLoad>
                    )
                })}
            </div>
        </>
    )

}

function Loading() {
    return (
        <div className="post loading">
            <h5>Loading...</h5>
        </div>
    )
}

export default Gallery;
